import React from "react";

//설치 파일: 
//npm install prop-types


class App extends React.Component {

  state = {
    isLoading: true,
  };

  componentDidMount() {
    setTimeout(() => {
      this.setState({isLoading: false})
    }, 6000);
  }

  render() {
    const { isLoading } = this.state;
    return (
      <div>
        {isLoading ? 'Loading.....' : 'We are ready'}
      </div>
    ) 
  }

}

export default App;
